mapper.select:
select id, member_email, member_password, member_name, member_status, created_datetime, updated_datetime
from tbl_member
where member_email = #{memberEmail}

select id, member_email, member_password, member_name, member_phone_number, member_status, created_datetime, updated_datetime
from tbl_member
where member_email = #{memberEmail} and member_password = #{memberPassword}
